import React, { Component } from "react";
import axios from "axios";
import { Card } from "react-bootstrap";
class Newcard extends Component {
  constructor(props) {
    super(props);

    this.state = {}

        values = async e => {
          e.preventdefault();
          const api_call = await fetch(`http://192.168.1.20:5000/doctype`);
          const data = await api_call.json();
          console.log(data);
        }



    // this.state = {};
    // values.forEach(v => (this.state[v] = ""));
    this.changeData = this.changeData.bind(this);
    this.postIt = this.postIt.bind(this);
    this.datedaat = this.datedaat.bind(this);
  }

  changeData(name, e) {
    this.setState({
      [name]: e.target.value
    });
  }
  postIt(e) {
    console.log(this.state);
    e.preventdefault();
  }

  datedaat(element) {
    console.log(this.state);
    return (
      <div className="row">
        <div className="col s8">
          <div className="card blue Darken-3">
            <div className="card-content">
              <form onSubmit={this.postIt}>
                <input
                  type="file"
                  name={element}
                  id="adharfile"
                  onChange={this.handleselectedFile}
                />
              </form>
            </div>
          </div>
        </div>
      </div>
    );
  }

  render() {
    let list = [];
    this.state.values.forEach(element => {
      list.push(this.datedaat(element));
    });
    return <div className="container">{list}</div>;
  }
}

export default Newcard;
