import React, { Component } from "react";
import axios from "axios";
import "./Newcard.css";
import { Card, CardTitle, Button, Row, CardGroup, Col, CardText, } from "reactstrap";


class Newcard extends Component {
    constructor(props) {
        super(props);

        this.state = {
            values: []
        };

        this.changeData = this.changeData.bind(this);
        this.postIt = this.postIt.bind(this);
        this.datedaat = this.datedaat.bind(this);
    }

    componentWillMount() {
        var token = localStorage.getItem("tokenId");
        console.log(localStorage.getItem("tokenId"))

        let headers = {
            "Control-Allow-Origin": "*",
            'Content-Type': 'application/x-www-form-urlencoded',
            'Accept': 'application/json',
            "tokenId": token
        }

        axios
            .get("http://192.168.1.20:8081/v1/doctype", headers


            )
            .then(response => {
                this.setState({
                    values: response.data
                });
            });


    }

    changeData(name, e) {
        this.state({
            [name]: e.target.value
        });
    }
    postIt(e) {
        console.log(this.state);
        e.preventdefault();
    }
    handleselectedFile = event => {
        this.setState({
            selectedFile: event.target.files[0],
            selectedFileType: event.target.id,
            loaded: 100
        });
    };
    handleUpload = event => {
        event.preventDefault();
        console.log(this.state.selectedFile);

        let token = window.localStorage.getItem("tokenId");
        console.log(token);
        let data = new FormData();
        data.append("file", this.state.selectedFile);
        data.append("name", "test");

        axios
            .post(
                "http://192.168.1.20:8080/v1/upload?fileType=" +
                this.state.selectedFileType,
                data,
                {
                    headers: {
                        //"Content-Type": "multipart/form-data",
                        "tokenId": token
                    }
                },
                {
                    onUploadProgress: ProgressEvent => {
                        this.setState({
                            loaded: (ProgressEvent.loaded / ProgressEvent.total) * 100
                        });
                    }
                }
            )
            .then(response => {
                (window.alert("Document uploaded Succesfully"));
            });
    };
    handleDownload = event => {
        event.preventDefault()
        let token = window.localStorage.getItem("tokenId");
        console.log(token)







        axios.get(
            "http://192.168.1.20:8080/v1/download?fileType="+ this.state.selectedFileType
            , {
                headers: {
                    "tokenId": token
                }

            }
        )
            .then((response) => response.Blob())
            .then((Blob) =>{
                console.log(Blob)
            })
    };

    datedaat(element) {

        console.log(this.state);

        return (
            <div className='container'>
                <Row sm='4'>
                    <Col sm="4">
                        <CardGroup>
                            <Card body inverse style={{ backgroundColor: '#333', borderColor: 'primary' }}>
                                <CardTitle>{element.displayName}</CardTitle>
                                <CardText><input type='file' id={element.fileType} onChange={this.handleselectedFile}></input></CardText>
                                <Button color='success' onClick={this.handleUpload}>Upload</Button>
                                <Button color='primary' onClick={this.handleDownload}>Download</Button>
                                <Button color='danger'>Archive</Button>
                            </Card>
                        </CardGroup>
                    </Col>

                </Row>

            </div>

        );
    }

    render() {
        let list = [];
        this.state.values.forEach(element => {
            list.push(this.datedaat(element));
        });
        return <div className="container">{list}</div>;
    }
}

export default Newcard;
