import React, { Component } from "react";
import { Card, Button, CardDeck } from "react-bootstrap";
import CloudUploadIcon from "@material-ui/icons/CloudUpload";



import "./App.css";
import axios from "axios";
const styles = theme => ({
  button: {
    margin: theme.spacing.unit,
  },
  leftIcon: {
    marginRight: theme.spacing.unit,
  },
  rightIcon: {
    marginLeft: theme.spacing.unit,
  },
  iconSmall: {
    fontSize: 20,
  },
});

class App extends Component {
  handleselectedFile = event => {
    this.setState({
      selectedFile: event.target.files[0],
      loaded: 100
    });
  };
  handleUpload = () => {
    console.log("uploaded");
    const data = new FormData();
    data.append("file", this.state.selectedFile, this.state.selectedFile.name);

    axios
      .post("http://192.168.1.20:5000/upload", data, {
        onUploadProgress: ProgressEvent => {
          this.setState({
            loaded: (ProgressEvent.loaded / ProgressEvent.total) * 100
          });
        }
      })
      .then(res => {
        console.log(res.statusText);
      });
  };
  render() {
    return (
      <div className="App">
        <CardDeck>
          <Card bg="primary">
            <Card.Img
              variant="top"
              src="/home/nineleaps/Desktop/upload-1118929_960_720.png"
            />
            <Card.Body>
              <Card.Title>Adhaar Card</Card.Title>
              <Card.Text />
            </Card.Body>
            <Card.Footer>
              <small className="text-dark">
                <input
                  type="file"
                  name=""
                  id=""
                  onChange={this.handleselectedFile}
                />
                <Button
                  variant="contained"
                  color="default"
                  className={styles.button}
                />
                Upload
                <CloudUploadIcon className={styles.rightIcon} />
              </small>
            </Card.Footer>
          </Card>
          <Card bg="primary">
            <Card.Img variant="top" src="holder.js/100px160" />
            <Card.Body>
              <Card.Title>Pan Card</Card.Title>
              <Card.Text />
            </Card.Body>
            <Card.Footer>
              <small className="dark">
                <input
                  type="file"
                  name=""
                  id=""
                  onChange={this.handleselectedFile}
                />
                <button onClick={this.handleUpload}>Upload</button>
              </small>
            </Card.Footer>
          </Card>
          <Card bg="primary">
            <Card.Img variant="top" src="holder.js/100px160" />
            <Card.Body>
              <Card.Title>ID Card</Card.Title>
              <Card.Text />
            </Card.Body>
            <Card.Footer>
              <small className="dark">
                <input
                  type="file"
                  name=""
                  id=""
                  onChange={this.handleselectedFile}
                />
                <button onClick={this.handleUpload}>Upload</button>
              </small>
            </Card.Footer>
          </Card>
          <Card bg="primary">
            <Card.Img variant="top" src="holder.js/100px160" />
            <Card.Body>
              <Card.Title>Marks Card</Card.Title>
              <Card.Text />
            </Card.Body>
            <Card.Footer>
              <small className="dark">
                <input
                  type="file"
                  name=""
                  id=""
                  onChange={this.handleselectedFile}
                />
                <button onClick={this.handleUpload}>Upload</button>
              </small>
            </Card.Footer>
          </Card>
          <Card bg="primary">
            <Card.Img variant="top" src="holder.js/100px160" />
            <Card.Body>
              <Card.Title>Marks Card</Card.Title>
              <Card.Text />
            </Card.Body>
            <Card.Footer>
              <small className="dark">
                <input
                  type="file"
                  name=""
                  id=""
                  onChange={this.handleselectedFile}
                />
                <button onClick={this.handleUpload}>Upload</button>
              </small>
            </Card.Footer>
          </Card>
          <Card bg="primary">
            <Card.Img variant="top" src="holder.js/100px160" />
            <Card.Body>
              <Card.Title>Marks Card</Card.Title>
              <Card.Text />
            </Card.Body>
            <Card.Footer>
              <small className="dark">
                <input
                  type="file"
                  name=""
                  id=""
                  onChange={this.handleselectedFile}
                />
                <button onClick={this.handleUpload}>Upload</button>
              </small>
            </Card.Footer>
          </Card>
        </CardDeck>
        ;
      </div>
    );
  }
}

export default App;
