import React, { Component } from "react";
import axios from "axios";
import "./Newcard.css";
import './Buttons'
import { Card, CardTitle, Button, Row, CardGroup, Col, CardText, } from "reactstrap";
import Buttons from "./Buttons";


class Newcard extends Component {
    constructor(props) {
        super(props);

        this.state = {
            values: [],
            disabled:'true'
        };

        this.changeData = this.changeData.bind(this);
        this.postIt = this.postIt.bind(this);
        this.datedaat = this.datedaat.bind(this);
    }

    componentWillMount() {
        var token = localStorage.getItem("tokenId");
        console.log(localStorage.getItem("tokenId"))

        let headers = {
            "Control-Allow-Origin": "*",
            'Content-Type': 'application/x-www-form-urlencoded',
            'Accept': 'application/json',
            "tokenId": token
        }

        axios
            .get("http://192.168.1.22:8080/v1/doctype", headers


            )
            .then(response => {
                this.setState({
                    values: response.data
                });
            });


    }

    changeData(name, e) {
        this.state({
            [name]: e.target.value
        });
    }
    postIt(e) {
        console.log(this.state);
        e.preventdefault();
    }
    handleselectedFile = event => {
        this.setState({
            selectedFile: event.target.files[0],
            selectedFileType: event.target.id,
            loaded: 100
        });
    };
    handleUpload = event => {
        event.preventDefault();
        console.log(this.state.selectedFile);

        let token = window.localStorage.getItem("tokenId");
        console.log(token);
        let data = new FormData();
        data.append("file", this.state.selectedFile);
        data.append("name", "test");

        axios
            .post(
                "http://192.168.1.22:8080/v1/upload?fileType=" +
                this.state.selectedFileType,
                data,
                {
                    headers: {
                        //"Content-Type": "multipart/form-data",
                        "tokenId": token
                    }
                },
                {
                    onUploadProgress: ProgressEvent => {
                        this.setState({
                            loaded: (ProgressEvent.loaded / ProgressEvent.total) * 100
                        });
                    }
                }
            )
            .then(response => {
                (window.alert("Document uploaded Succesfully"));
            });
    };
    handleDownload = event => {
        event.preventDefault()
        let token = window.localStorage.getItem("tokenId");
        console.log(token)





        axios.get(
            "http://192.168.1.22:8080/v1/download?fileType=" + this.state.selectedFileType
            , {
                headers: {
                    "tokenId": token
                },


            }
        )
        .then(response =>{
            console.log(response)
        })
    }   

    handleDelete = event =>{
        event.preventDefault()
        let token = window.localStorage.getItem("tokenId");
        console.log(token)

        axios.get(
            "http://192.168.1.22:8080/v1/download?fileType=" + this.state.selectedFileType
            , {
                headers: {
                    "tokenId": token
                },


            }
        )



        .then(response => {
            const filename =  response.headers.get('Content-Disposition').split('filename=')[1];
            response.blob().then(blob => {
              let url = window.URL.createObjectURL(blob);
              let a = document.createElement('a');
              a.href = url;
              a.download = filename;
              a.click();
          });
       });

    }


    datedaat(element) {

        console.log(this.state);

        return (
            <div className='container'>
               <Buttons/>
            </div>

        );
    }

    render() {
        let list = [];
        this.state.values.forEach(element => {
            list.push(this.datedaat(element));
        });
        return <div className="container">{list}</div>;
    }
}

export default Newcard;
