import React, { Component } from "react";
import axios from "axios";
import { Card } from "react-bootstrap";
import { Button, } from "react-bootstrap";
import CloudUploadIcon from "@material-ui/icons/CloudUpload";
class Newcard extends Component {
  constructor(props) {
    super(props);

    this.state = {
      values: []
      // ["Adhar Card", "Pan Card", "Marks Card", "DOJ", "DOB"]
    };

    // this.state = {};
    // values.forEach(v => (this.state[v] = ""));
    this.changeData = this.changeData.bind(this);
    this.postIt = this.postIt.bind(this);
    this.datedaat = this.datedaat.bind(this);
  }

  componentWillMount() {
    axios.get("http://192.168.1.154:8080/v1/doctype").then(response => {
      this.setState({
        values: response.data
      });
    });
  }

  changeData(name, e) {
    this.setState({
      [name]: e.target.value
    });
  }
  postIt(e) {
    console.log(this.state);
    e.preventdefault();
  }
  handleselectedFile = event => {
    this.setState({
      selectedFile: event.target.files[0],
      selectedFileType: event.target.id,
      loaded: 100
    });
  };
  handleUpload = event => {
    event.preventDefault();
    console.log(this.state.selectedFile);
    console.log

    let data = new FormData();
    data.append("file", this.state.selectedFile);
    data.append("name", "test");

    axios
      .post(
        "http://192.168.1.154:8080/v1/upload?fileType=" +
          this.state.selectedFileType,
        data,
        {
          headers: {
            "Content-Type": "multipart/form-data",

            "tokenId":"gapi.auth2.getAuthInstance().currentUser.get().getAuthResponse().id_token"


          }
        },
        {
          onUploadProgress: ProgressEvent => {
            this.setState({
              loaded: (ProgressEvent.loaded / ProgressEvent.total) * 100
            });
          }
        }
      )

      .then(res => {
        console.log(res.statusText);
      });
  };

  datedaat(element) {
    console.log(this.state);
    return (
      <div className="flex flex-wrap -m-3 w-full sm:w-1/2 md:w-1/3 flex flex-col p-3 bg-white rounded-lg shadow-lg overflow-hidden flex-1 flex flex-col">
        <Card bg="danger">
          <Card.Body>
            <Card.Title>{element.displayName}</Card.Title>
            <Card.Text />
          </Card.Body>
          <Card.Footer>
            <small className="text-dark">
              {
                <input
                  type="file"
                  name="adhar"
                  id={element.fileType}
                  onChange={this.handleselectedFile}
                />
              }
              <Button
                variant="contained"
                color="default"
                onClick={this.handleUpload}
              >
                Upload
                <CloudUploadIcon />
              </Button>

              <button className="btn" onClick={this.handleDownload}>
                <i className="fa fa-download" /> Download
              </button>
            </small>
          </Card.Footer>
        </Card>
      </div>
    );
  }

  render() {
    let list = [];
    this.state.values.forEach(element => {
      list.push(this.datedaat(element));
    });
    return <div className="container">{list}</div>;
  }
}

export default Newcard;
