import React, { Component } from "react";
import axios from "axios";
import { Card } from "react-bootstrap";
import { Button, CardDeck } from "react-bootstrap";
import CloudUploadIcon from "@material-ui/icons/CloudUpload";
class Newcard extends Component {
  constructor(props) {
    super(props);

    this.state = {
      values:[]
      // ["Adhar Card", "Pan Card", "Marks Card", "DOJ", "DOB"]
    };

    // this.state = {};
    // values.forEach(v => (this.state[v] = ""));
    this.changeData = this.changeData.bind(this);
    this.postIt = this.postIt.bind(this);
    this.datedaat = this.datedaat.bind(this);
  }

  changeData(name, e) {
    this.setState({
      [name]: e.target.value
    });
  }
  postIt(e) {
    console.log(this.state);
    e.preventdefault();
  }

  datedaat() {
    console.log(this.state);
    return (
      <div className="flex flex-wrap -m-3 w-full sm:w-1/2 md:w-1/3 flex flex-col p-3 bg-white rounded-lg shadow-lg overflow-hidden flex-1 flex flex-col">
            <Card bg="danger">
              <Card.Body>
                <Card.Title>Adhaar Card</Card.Title>
                <Card.Text />
              </Card.Body>
              <Card.Footer>
                <small className="text-dark">
                  {
                    <input
                      type="file"
                      name="adhar"
                      id="adharfile"
                      onChange={this.handleselectedFile}
                    />
                  }
                  <Button
                    variant="contained"
                    color="default"
                    onClick={this.handleUpload}
                  >
                    Upload
                    <CloudUploadIcon />
                  </Button>

                  <button className="btn" onClick={this.handleDownload}>
                    <i className="fa fa-download" /> Download
                  </button>
                </small>
              </Card.Footer>
            </Card>
          </div>
    );
  }

  render() {
    let list = [];
    this.state.values.forEach(element => {
      list.push(this.datedaat(element));
    });
    return <div className="container">{list}</div>;
  }
}

export default Newcard;
